catalog_script_client.script:
function onSubmit() {
    if (g_form.getValue('bypass_credit_check') == 'true') {
        return true;
    }

    var rawCosto = g_form.getValue('costo_totale') || "";
    var costoPulito = rawCosto.replace(/[^\d.,]/g, '').replace(',', '.');
    var costoTotale = parseFloat(costoPulito);

    if (isNaN(costoTotale)) {
          g_form.addErrorMessage("Errore: valore costo totale non valido. Letto: " + rawCosto);
        return false;
    }

    var ga = new GlideAjax('ControlloCreditoUtente');
    ga.addParam('sysparm_name', 'getUserCredit'); 
    ga.addParam('sysparm_user_id', g_user.userID);

    ga.getXMLAnswer(function(answer) {
        var creditoUtente = parseFloat(answer);

        if (isNaN(creditoUtente)) {
              g_form.addErrorMessage("Errore: impossibile recuperare il credito.");
            return;
        }

        if (creditoUtente < costoTotale) {
              g_form.addErrorMessage("Credito insufficiente per effettuare la prenotazione.");
            return;
        }


        g_form.setValue('bypass_credit_check', 'true');
        g_form.submit(); 
    });

    return false; 
}
